_str_ME_obmeSelection_table *fwif_MEMC_get_ME_obmeSelection_table(unsigned char level)
{
	const _PQLCONTEXT *s_pContext = GetPQLContext();
		unsigned char	load_tyep_all = 255;
	
		if( level < obmeSelection_table_Num ){
			load_tyep_all = ME_obmeSelection_table[level].ctrl_tyep_all;
		}else{ 
			load_tyep_all = CTRL_Default;
		}
	
		if( load_tyep_all == CTRL_Default ){ //Load Default setting
			ME_obmeSelection_table[0].ctrl_tyep_all 					= load_tyep_all;
			ME_obmeSelection_table[0].vbuf_ip_ac_obme_mode				= s_pContext->_output_FRC_LgDet.DefaultVal.me1_ip_ac_obme_mode;
			ME_obmeSelection_table[0].vbuf_ip_dc_obme_mode				= s_pContext->_output_FRC_LgDet.DefaultVal.me1_ip_dc_obme_mode;
			ME_obmeSelection_table[0].vbuf_ip_dc_obme_mode_sel			= s_pContext->_output_FRC_LgDet.DefaultVal.me1_ip_dc_obme_mode_sel;
			ME_obmeSelection_table[0].vbuf_pi_ac_obme_mode				= s_pContext->_output_FRC_LgDet.DefaultVal.me1_pi_ac_obme_mode;
			ME_obmeSelection_table[0].vbuf_pi_dc_obme_mode				= s_pContext->_output_FRC_LgDet.DefaultVal.me1_pi_dc_obme_mode;
			ME_obmeSelection_table[0].vbuf_pi_dc_obme_mode_sel				= s_pContext->_output_FRC_LgDet.DefaultVal.me1_pi_dc_obme_mode_sel;
			
			ME_obmeSelection_table[0].kme_me2_vbuf_1st_ac_obme_mode 		= s_pContext->_output_FRC_LgDet.DefaultVal.me2_1st_ac_obme_mode;
			ME_obmeSelection_table[0].kme_me2_vbuf_1st_dc_obme_mode 		= s_pContext->_output_FRC_LgDet.DefaultVal.me2_1st_dc_obme_mode;
			ME_obmeSelection_table[0].kme_me2_vbuf_1st_dc_obme_mode_sel 	= s_pContext->_output_FRC_LgDet.DefaultVal.me2_1st_dc_obme_mode_sel;
			ME_obmeSelection_table[0].kme_me2_vbuf_2nd_ac_obme_mode 		= s_pContext->_output_FRC_LgDet.DefaultVal.me2_2nd_ac_obme_mode;
			ME_obmeSelection_table[0].kme_me2_vbuf_2nd_dc_obme_mode 		= s_pContext->_output_FRC_LgDet.DefaultVal.me2_2nd_dc_obme_mode;
			ME_obmeSelection_table[0].kme_me2_vbuf_2nd_dc_obme_mode_sel 	= s_pContext->_output_FRC_LgDet.DefaultVal.me2_2nd_dc_obme_mode_sel;	
		}
	
		if(load_tyep_all != CTRL_Default){
			return (_str_ME_obmeSelection_table *)&ME_obmeSelection_table[level];
		}else{ /*load default */
			return (_str_ME_obmeSelection_table *)&ME_obmeSelection_table[0];
		}
}
