VOID ME_obme1Selection_wrtAction(const _PARAM_WRT_COM_REG *pParam)
{
	const _PQLCONTEXT *s_pContext = GetPQLContext();
	//_PQLPARAMETER *s_pParam       = GetPQLParameter();
	unsigned char table_level = 255;
	unsigned int u32_RB_sc;
	static unsigned char sc_hold_cnt = 0;
	unsigned int hold_cnt_th = 20;
	unsigned int log_en = 0;
	unsigned int u1_force_table_en, u3_force_table_id;
	_str_ME_obme1Selection_table *tmp_ME_obme1Selection_table = fwif_MEMC_get_ME_obme1Selection_table(0);
	signed short gmv_bgx,gmv_bgy;
    signed short gmv_bgcnt;
	ReadRegister(KME_ME1_TOP1_ME1_WLC1_00_reg, 0, 5, &u32_RB_sc);
	ReadRegister(SOFTWARE2_SOFTWARE2_29_reg, 0, 8, &hold_cnt_th);
	ReadRegister(SOFTWARE2_SOFTWARE2_29_reg, 31, 31, &log_en);
	ReadRegister(SOFTWARE2_SOFTWARE2_29_reg, 13, 15, &u3_force_table_id);
	ReadRegister(SOFTWARE2_SOFTWARE2_29_reg, 12, 12, &u1_force_table_en);
	if(hold_cnt_th ==0){
		hold_cnt_th = 20;
	}	
	gmv_bgcnt = s_pContext->_output_read_comreg.me1_gmv_bgcnt;
	gmv_bgx = s_pContext->_output_read_comreg.dh_gmv_bgx;
	gmv_bgy = s_pContext->_output_read_comreg.dh_gmv_bgy;

	if((u32_RB_sc!=0 || sc_hold_cnt!=0) && log_en){
		rtd_pr_memc_emerg("[%s][%d]u32_RB_sc =  %d [,%d,%d,] [%d], [%d], [%d,%d,%d]",__FUNCTION__, __LINE__,u32_RB_sc, sc_hold_cnt,s_pContext->_output_read_comreg.u1_sc_status_rb
																			,dehalo_occl_unstable_check,s_pContext->_output_me_sceneAnalysis.u2_panning_flag,gmv_bgcnt,gmv_bgx,gmv_bgy);
	}


	if (pParam->u1_dynamic_obme_sel_wrt_en == 1)
	{		
		
		if ((u32_RB_sc != 0) || (dehalo_occl_unstable_check==1)){
			sc_hold_cnt = hold_cnt_th;			
		}else{
			if(sc_hold_cnt>0){
				sc_hold_cnt--;
				if( (gmv_bgcnt>25000) && (_ABS_(gmv_bgx)<5) && (_ABS_(gmv_bgy)<5) && sc_hold_cnt>5){
					sc_hold_cnt = sc_hold_cnt - 5;
				}
			}
		}

		if(sc_hold_cnt!=0){
			table_level = 1;
		}else if (s_pContext->_output_me_sceneAnalysis.u2_panning_flag == 1 ){
			table_level = 5;
		}else{
			table_level = 0;
		}
		
		if(u1_force_table_en){
			table_level = (u3_force_table_id>=obme1Selection_table_Num) ? 0 : u3_force_table_id;
		}
		
		tmp_ME_obme1Selection_table = fwif_MEMC_get_ME_obme1Selection_table(table_level);

		if(tmp_ME_obme1Selection_table != NULL){
			ME_obme1_Selection_wrtRegister(tmp_ME_obme1Selection_table);
		}		
	}else{
		sc_hold_cnt = 0;
	}

}

