VOID  ME_Scan_wrtRegister(_str_ME_Scan_table *ME_Scan_table)
{
	kme_top_kme_top_08_RBUS kme_top_kme_top_08;
	kme_vbuf_top_kme_vbuf_top_30_RBUS kme_vbuf_top_kme_vbuf_top_30;
	kme_vbuf_top_kme_me1_multi_scan_RBUS kme_vbuf_top_kme_me1_multi_scan;
	kme_me2_vbuf_top_kme_me2_vbuf_top_2c_RBUS kme_me2_vbuf_top_kme_me2_vbuf_top_2c;

	kme_top_kme_top_08.regValue = rtd_inl(KME_TOP_KME_TOP_08_reg);
	kme_vbuf_top_kme_vbuf_top_30.regValue = rtd_inl(KME_VBUF_TOP_KME_VBUF_TOP_30_reg);
	kme_vbuf_top_kme_me1_multi_scan.regValue = rtd_inl(KME_VBUF_TOP_KME_ME1_MULTI_SCAN_reg);
	kme_me2_vbuf_top_kme_me2_vbuf_top_2c.regValue = rtd_inl(KME_ME2_VBUF_TOP_KME_ME2_VBUF_TOP_2C_reg);

	kme_top_kme_top_08.reg_kphase_me_work_mode = ME_Scan_table->kphase_me_work_mode;
	kme_vbuf_top_kme_vbuf_top_30.kme_me_work_mode = ME_Scan_table->kme_me_work_mode;
	kme_vbuf_top_kme_me1_multi_scan.me_work_mode_multi_scan = ME_Scan_table->me_work_mode_multi_scan;
	kme_me2_vbuf_top_kme_me2_vbuf_top_2c.kme_me2_vbuf_doublescan_en = ME_Scan_table->kme_me2_vbuf_doublescan_en;

	rtd_outl(KME_TOP_KME_TOP_08_reg, kme_top_kme_top_08.regValue);
	rtd_outl(KME_VBUF_TOP_KME_VBUF_TOP_30_reg, kme_vbuf_top_kme_vbuf_top_30.regValue);
	rtd_outl(KME_VBUF_TOP_KME_ME1_MULTI_SCAN_reg, kme_vbuf_top_kme_me1_multi_scan.regValue);
	rtd_outl(KME_ME2_VBUF_TOP_KME_ME2_VBUF_TOP_2C_reg, kme_me2_vbuf_top_kme_me2_vbuf_top_2c.regValue);
}

VOID ME_Scan_wrtAction(const _PARAM_WRT_COM_REG *pParam, _OUTPUT_WRT_COM_REG *pOutput)
{
	unsigned int force_en, force_type;
	_str_ME_Scan_table* _ME_Scan_table = NULL;
	ReadRegister(SOFTWARE2_SOFTWARE2_18_reg, 31, 31, &force_en);//ME_Scan_wrtAction's force_en
	ReadRegister(SOFTWARE2_SOFTWARE2_18_reg, 24, 25, &force_type);//ME_Scan_wrtAction's force_type

	if(!force_en){
		return;
	}

	_ME_Scan_table = fwif_MEMC_get_ME_Scan_table(force_type);

	ME_Scan_wrtRegister(_ME_Scan_table);
}


