VOID Clear_LogoRim_WrtAction(_OUTPUT_RimCtrl *pRimInfo, _OUTPUT_WRT_COM_REG *pOutput)
{
#if (IC_K24)
	kme_logo3_km_logo_blk_feature_rim_check_1_RBUS kme_logo3_km_logo_blk_feature_rim_check_1;

	unsigned int u8_rim_diff_th, u8_stable_rim_diff_th, algo_en, log_en, plot_en;
	unsigned short u16_debug_id = 0;
	bool u1_apply = false;

	kme_logo3_km_logo_blk_feature_rim_check_1.regValue = rtd_inl(KME_LOGO3_km_logo_blk_feature_rim_check_1_reg);

	ReadRegister(SOFTWARE2_SOFTWARE2_41_reg,  0,  7, &u8_rim_diff_th);
	ReadRegister(SOFTWARE2_SOFTWARE2_41_reg, 24, 28, &u8_stable_rim_diff_th);
	ReadRegister(SOFTWARE2_SOFTWARE2_41_reg, 29, 29, &plot_en);
	ReadRegister(SOFTWARE2_SOFTWARE2_41_reg, 30, 30, &log_en);
	ReadRegister(SOFTWARE2_SOFTWARE2_41_reg, 31, 31, &algo_en);

	if(!algo_en){
		return;
	}
	// convert param by resolution
	u8_rim_diff_th = Convert_UHD_Param_To_Current_RES(u8_rim_diff_th, PARAM_DIRECTION_Y);
	u8_stable_rim_diff_th = Convert_UHD_Param_To_Current_RES(u8_stable_rim_diff_th, PARAM_DIRECTION_Y);

	// clear logo in rim when real rim is far from hw rim and real rim is stable. Stream: #50
	if(pRimInfo->u12_Rim_HW[_RIM_TOP] > pRimInfo->u12_Rim_Result[_RIM_TOP] + u8_rim_diff_th && _ABS_DIFF_(pRimInfo->u12_Rim_Pre[_RIM_TOP], pRimInfo->u12_Rim_Result[_RIM_TOP]) < u8_stable_rim_diff_th){
		u16_debug_id = u16_debug_id | 0xf;
		u1_apply = true;
		kme_logo3_km_logo_blk_feature_rim_check_1.km_logo_blk_feature_pxlrim_v0 = (pRimInfo->u12_Rim_HW[_RIM_TOP] + 2)>>2;
		if(plot_en){
			WriteRegister(KPOST_TOP_KPOST_TOP_00_reg, 13, 13, 1);
			WriteRegister(KPOST_TOP_KPOST_TOP_00_reg, 15, 15, 1);
			WriteRegister(KPOST_TOP_KPOST_TOP_00_reg, 0, 12, 1);
			WriteRegister(KPOST_TOP_KPOST_TOP_00_reg, 16, 28, pRimInfo->u12_Rim_HW[_RIM_TOP]);


			WriteRegister(MC_MC_CC_reg, 25, 25, 1);
			WriteRegister(MC_MC_CC_reg, 27, 27, 1);
			WriteRegister(MC_MC_CC_reg,  0, 23, 0xff004c);
			WriteRegister(MC_MC_C8_reg,  0, 11, 1);
			WriteRegister(MC_MC_C8_reg, 16, 27, pRimInfo->u12_Rim_Result[_RIM_TOP]);
		}
	}else{
		kme_logo3_km_logo_blk_feature_rim_check_1.km_logo_blk_feature_pxlrim_v0 = 0;
		if(plot_en){
			WriteRegister(KPOST_TOP_KPOST_TOP_00_reg, 13, 13, 0);
			WriteRegister(KPOST_TOP_KPOST_TOP_00_reg, 15, 15, 0);


			WriteRegister(MC_MC_CC_reg, 25, 25, 0);
			WriteRegister(MC_MC_CC_reg, 27, 27, 0);
		}
	}

	if(pRimInfo->u12_Rim_HW[_RIM_BOT]+ u8_rim_diff_th < pRimInfo->u12_Rim_Result[_RIM_BOT] && _ABS_DIFF_(pRimInfo->u12_Rim_Pre[_RIM_BOT], pRimInfo->u12_Rim_Result[_RIM_BOT]) < u8_stable_rim_diff_th){
		u16_debug_id = u16_debug_id | 0xf0;
		u1_apply = true;
		kme_logo3_km_logo_blk_feature_rim_check_1.km_logo_blk_feature_pxlrim_v1 = (pRimInfo->u12_Rim_HW[_RIM_BOT] + 2)>>2;
	}else{
		kme_logo3_km_logo_blk_feature_rim_check_1.km_logo_blk_feature_pxlrim_v1 = 0;
	}

	kme_logo3_km_logo_blk_feature_rim_check_1.km_logo_blk_feature_rim_check_en = u1_apply;

	rtd_outl(KME_LOGO3_km_logo_blk_feature_rim_check_1_reg, kme_logo3_km_logo_blk_feature_rim_check_1.regValue);

	if(log_en){
		rtd_pr_memc_emerg("debug,%x, HW,%d,%d,  Rim,%d,%d\n", u16_debug_id, pRimInfo->u12_Rim_HW[_RIM_TOP], pRimInfo->u12_Rim_HW[_RIM_BOT], pRimInfo->u12_Rim_Result[_RIM_TOP], pRimInfo->u12_Rim_Result[_RIM_BOT]);
	}
#else
	return;
#endif
}

