	_str_MC_Local_Deflicker_table tmp_MC_Local_Deflicker_table;
	tmp_MC_Local_Deflicker_table.wrt_byphase_en= s_pContext->_output_me_sceneAnalysis.u1_wrt_byphase_en;
	tmp_MC_Local_Deflicker_table.wrt_poly_en= s_pContext->_output_me_sceneAnalysis.u1_wrt_poly_en;
	tmp_MC_Local_Deflicker_table.wrt_mc_local_lpf_en = s_pContext->_output_me_sceneAnalysis.u1_wrt_local_lpf_en;
	
	if(s_pContext->_output_me_sceneAnalysis.u1_local_deflicker_en == 0)
		return;

	if(s_pContext->_output_me_sceneAnalysis.u1_single_side_byphase_en == 1){
		tmp_MC_Local_Deflicker_table.mc_sobj_en = 0;
		tmp_MC_Local_Deflicker_table.mc_bld_mvd_y_max = 0;
		tmp_MC_Local_Deflicker_table.mc_bld_mvd_y_min = 0;
		tmp_MC_Local_Deflicker_table.mc_bld_w_type = 4;
	}
	else{
		tmp_MC_Local_Deflicker_table.mc_sobj_en = 1;
		tmp_MC_Local_Deflicker_table.mc_bld_mvd_y_max = 0xff;
		tmp_MC_Local_Deflicker_table.mc_bld_mvd_y_min = 0x80;
		tmp_MC_Local_Deflicker_table.mc_bld_w_type = 3; 	   
	}
	tmp_MC_Local_Deflicker_table.mc_var_lpf_en = 3;
	if(s_pContext->_output_me_sceneAnalysis.u8_lbmc_local_lpf_y3 < 7){
		tmp_MC_Local_Deflicker_table.force_iz_when_mc_phase_eq0 = 1; // no need to apply lpf, enable this function to save BW
	}else{
	tmp_MC_Local_Deflicker_table.force_iz_when_mc_phase_eq0 = 0;
	}
	tmp_MC_Local_Deflicker_table.lbmc_z_replace_chv = 0;

	tmp_MC_Local_Deflicker_table.lbmc_local_lpf_me1_dtl_coef = s_pContext->_output_me_sceneAnalysis.u8_me1_max_dtl;
	MC_Local_Deflicker_wrtRegister((_str_MC_Local_Deflicker_table *)&tmp_MC_Local_Deflicker_table, (_OUTPUT_ME_SCENE_ANALYSIS *)&(s_pContext->_output_me_sceneAnalysis));

