void Change_MEMC_switch_table(void)
{
	unsigned int by_table_by_reg=0,i=0;
	unsigned int tmp_Write_ComReg=0;
	unsigned int chip_table=0;

	ReadRegister(SOFTWARE2_SOFTWARE2_12_reg, 0, 0, &by_table_by_reg);
	ReadRegister(SOFTWARE2_SOFTWARE2_12_reg, 1, 3, &chip_table);

	
	if(by_table_by_reg)//by table
	{
		for(i=0;i<32;i++)
		{
			MEMC_switch_table[0][i]=tmp_MEMC_switch_table[chip_table][0][i];//Write_ComReg
		}
		WriteRegister(SOFTWARE2_SOFTWARE2_12_reg, 17, 17, 1);
	}
	else{//switch by reg		
		for(i=0;i<32;i++)
		{
			ReadRegister(SOFTWARE2_SOFTWARE2_13_reg, i, i, &tmp_Write_ComReg);

			tmp_MEMC_switch_table[2][0][i]=tmp_Write_ComReg;
			MEMC_switch_table[0][i]=tmp_MEMC_switch_table[2][0][i];		
		}
		WriteRegister(SOFTWARE2_SOFTWARE2_12_reg, 16, 16, 1);
	}
	
}

void update_switch_on_off(void)
{	
	Change_MEMC_switch_table();
}


